[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<table width="90%">
    <tr>
        <td style="text-align:left; width:20%">
            {% set logo = frappe.get_all('File', filters={'attached_to_doctype': 'Company', 'attached_to_name': doc.company}, fields=['file_name', 'file_url']) %}
            <img align="left" height="80px" src="{{ logo[0].file_url }}" alt="{{ logo[0].file_name }}" width="50px">
        </td>
        <td style="font-size:24px; text-align:center; width:70%">
            <br>
            <b>Purchase Order</b>
        </td>
    </tr>
</table>
"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<html>
    <head>
        <style>
            table{
              border-collapse: collapse;
              background-color: white;
              margin-top: 0px;
              width :100%;
              font-family:'HelveticaNeue LT 45 Light';
              font-size:12px;
            }
            ::-webkit-scrollbar {
            display: none;
        }
        tr {
            height: 3px; /* Adjust the height value as needed */
        }
        td {
            line-height: 12px;/* Adjust the line height value as needed. */
            valign: top;
            text-align:left;
        }

        @media print 
        {
            .class 
            {
                background-color: #1a4567 !important;
                -webkit-print-color-adjust: exact;
            }
        }
            .footer {
                position: relative;
                bottom: 0;
                text-align: left; /* Center-align footer content */
                padding: 10px; /* Add padding for better appearance */
            }

        </style>
    </head>
    <body>
        <table width=100%>
            <tr>
                <td width=55%><b>PO Number</b>: {{doc.name}}</td>
                <td><b>Vendor Name</b>: {{doc.supplier_name}}</td>
            </tr>
            <tr>
                <td><b>PO Date</b> : {{doc.transaction_date}}
                <br>
                <br>
                <b>Department:{{doc.custom_department or ""}}</b>
                </td>
                <td><b>Address</b>:{{doc.supplier_address or ""}}<br>{{doc.address_display or ""}}
                <br>
                <b>PIC</b></td>
            </tr>
        </table>
        <table border=0px style="font-size:10px;">
            <tr style="border-bottom: 2px solid black;width:100%">
                <td width="5%"><b>NO</b></td>
                <td width="40%"><b>ITEMS</b></td>
                <td width="10%"><b>QTY</b></td>
                <td width="10%"><b>UNIT</b></td>
                <td width="17%"><b>UNIT PRICE</b></td>
                <td width="18%"><b>TOTAL UNIT PRICE</b></td>
            </tr>
            {% for item in doc.items%}
            <tr style="border-bottom: 2px solid black;width:100%">
                <td>{{item.idx}}</td>   
                <td>{{item.item_code}}</td>
                <td>{{item.qty}}</td>
                <td>{{item.uom}}</td>
                <td>{{item.rate}}</td>
                <td>{{item.amount}}</td>
            </tr>
            {%endfor%}
            <tr>
                <td colspan="4" style="border-bottom-style: hidden;border-left-style: hidden;"></td>
                <td><b>SUB TOTAL</b></td>
                <td>{{doc.total or 0.00}}</td>
            </tr>
            {% if doc.custom_management_fee != 0 %}
            <tr>
                <td colspan="4" style="border-bottom-style: hidden; border-left-style: hidden;"></td>
                <td><b>MANAGEMENT FEE</b></td>
                <td></td>
            </tr>
            {% endif %}

            {% if doc.custom_ppn != 0 %}
            <tr>
                <td colspan="4" style="border-bottom-style: hidden; border-left-style: hidden;"></td>
                <td><b>PPN</b></td>
                <td></td>
            </tr>
            {% endif %}

            <tr>
                <td colspan="4" style="border-bottom-style: hidden;border-left-style: hidden;"></td>
                <td><b>GRAND TOTAL</b></td>
                <td>{{doc.grand_total or ""}}</td>
            </tr>
        </table>
        <br>
        <table width=80% border=0px style="">
            <tr>
                <td width=20% ><b>1.Invoice Delivery</b></td>
                <td>{{doc.billing_address or ""}} <br> {{doc.billing_address_display or ""}}</td>
            </tr>
            <tr>
                <td><b>2.Delivery or Goods/Services</b></td>
                <td>{{doc.shipping_address}}{{doc.shipping_address_display or ""}}</td>
            </tr>
            <tr>
                <td><b>3. Terms & Conditions</b></td>
                <td style="line-height: 5px;">{{doc.tc_name or ""}}<br>{{doc.terms or ""}}</td>
            </tr>
            {% if doc.docstatus==1 %}
            <tr>
                <td></td>
                <td><b>"Confirmed By</b>
                <br>
                {% set files = frappe.get_all('File', filters={'attached_to_doctype': 'User', 'attached_to_name': frappe.session.user}, fields=['file_name', 'file_url']) %}
                {%- for file in files -%}
                <p style="text-align:left;"><img src="{{ file.file_url}}" alt="{{ file.file_name }}"/ height=80px width=80px></p>
                {%- endfor -%}

                {{frappe.db.get_value("User", doc.custom_approved_by, "full_name")}}<br>
                {{frappe.db.get_value("Employee", {"user_id": doc.custom_approved_by}, "designation") or ""}}
                </td>
            </tr>
            {% endif %}
        </table>
    </body>
</html>"}]
